00049 void IRV::processHeaders(){
00050     if(getHeader().size() != 5) {
00051         cout << RED <<"ERROR. Unexpected header size\nExpected 5, found " << getHeader().size() << '\n' << RESET;
00052         exit(-1);
00053     }
00054     setElectionType(getHeader()[0]);
00055     setNumberOfCandidates(stoi(getHeader()[1]));
00056     setNumberOfSeats(stoi(getHeader()[2]));
00057     addBallots(stoi(getHeader()[3]));
00058     
00059     stringstream cStream(getHeader()[4]);
00060     while(cStream.good()){
00061         string candidate;
00062         string party;
00063         getline(cStream, candidate, '/');
00064         getline(cStream, party, ',');
00065         addCandidate(Candidate(candidate, party)); //add the candidate to the candidates vector         
00066     }
00067 }
