00125 void IRV::runOff(){
00126     
00127     // ****************************************************************************
00128     // *            Number of combinations associated with a candidate            *
00129     // ****************************************************************************
00130 
00131     int nBlockSize = Combinations::sizeOf(getNumCandidates()) / getNumCandidates(); 
00132      
00133     int *nToTally = new int[nBlockSize]; 
00134 
00135     // ****************************************************************************
00136     // *                          Calculate Droop Quota                           *
00137     // ****************************************************************************
00138 
00139     setDroopQuota(( getNumBallots() / ( getNumSeats() + 1 ) ) + 1);
00140 
00141     // ****************************************************************************
00142     // *                              Run Droop Quota                             *
00143     // ****************************************************************************
00144     
00145     for(int nCount = 0; nCount < (getNumCandidates() - getNumSeats()); nCount++){ // TODO: ensure this is correct 
00146 
00147         // ****************************************************************************
00148         // *                           Log Round of Tallies                           *
00149         // ****************************************************************************
00150 
00151         logCandidates(); 
00152 
00153         // ****************************************************************************
00154         // *                   Check if droop quota has been reached                  *
00155         // *         ( also find candidate with fewest votes while I'm at it)         *
00156         // ****************************************************************************
00157 
00158         int nLowestCandidate = -1; 
00159         unsigned int cLowestVote = 999999; 
00160         for(int i = 0; i < getNumCandidates(); i++){ 
00161             if(getCandidates()[i].isLoser()){ continue; }
00162             
00163             if(getCandidates()[i].getNumVotes() == cLowestVote) { nLowestCandidate = flipCoin() == true ? i : nLowestCandidate;  } // Flip for loser 
00164             
00165             else if(getCandidates()[i].getNumVotes() < cLowestVote) { nLowestCandidate = i; cLowestVote = getCandidates()[i].getNumVotes();  } // Find lowest candidate 
00166             
00167             if(getCandidates()[i].getNumVotes() >= getDroopQuota()) { 
00168                 addWinner(&getCandidates()[i]);
00169                 if(getNumWinners() == getNumSeats()) { handleWin(); return; } // Stop runoff 
00170             } 
00171         } 
00172         
00173         // ****************************************************************************
00174         // *                               Update Loser                               *
00175         // ****************************************************************************
00176         getCandidates()[nLowestCandidate].setVotes(0); 
00177  
00178         addLoser(&getCandidates()[nLowestCandidate]); 
00179         
00180         addAuditLine("Eliminated: " + getCandidates()[nLowestCandidate].getName() + " with " + to_string(getCandidates()[nLowestCandidate].getNumVotes()) + " votes.");
00181         
00182         // ****************************************************************************
00183         // *                              Compute Runoff                              *
00184         // ****************************************************************************
00185         // Redistribute lowest candidate's votes (randomly) to everyone who is not a winner
00186         // Check if transferred vote makes a candidate reach droop, if so
00187         // add them to the winners list
00188 
00189         int nOffset = (nBlockSize * nLowestCandidate); 
00190  
00191         nOffset += nOffset == 0 ? 0 : 1; 
00192 
00193         // ****************************************************************************
00194         // *         Create an array of indices from offset, nBlockSize size.         *
00195         // *      Shuffle elements to create a random order to select tallies in      *
00196         // ****************************************************************************
00197 
00198         for(int i = 0; i < nBlockSize; i++) { nToTally[i] = i + nOffset; } // fill array start to end. IMPORTANT! Skipping first index! 
00199         
00200         if(SHUFFLE == true){
00201             srand(time(0));
00202             for(int i = 0; i < nBlockSize * 5; i++){ 
00203                 // Generate two random indices  
00204                 int nRandIdx1 = (rand() % (nBlockSize - 1)); 
00205                 int nRandIdx2 = (rand() % (nBlockSize - 1)); 
00206                  
00207                 // Swap elements 
00208                 int nTemp = nToTally[nRandIdx1]; 
00209      
00210                 nToTally[nRandIdx1] = nToTally[nRandIdx2]; 
00211                 nToTally[nRandIdx2] = nTemp; 
00212             } 
00213         }
00214 
00215         // ****************************************************************************
00216         // *                               Random Tally                               *
00217         // *                     Mathematically determine segments                    *
00218         // ****************************************************************************
00219 
00220         for(int i = 0; i < nBlockSize; i++){ 
00221             int nRank = Combinations::getRankingNumber(nToTally[i], getNumCandidates()); 
00222  
00223             for(int nPreference = nCount; nPreference < getNumCandidates() - 1; nPreference++){ 
00224                  
00225                 int nCandidate = (( nRank / quick_pow10(getNumCandidates() - nPreference - 2) ) % 10) - 1; 
00226                  
00227                 if( nCandidate != -1 && !getCandidates()[nPreference].isWinner() && !getCandidates()[nPreference].isLoser()){ 
00228                     
00229                     getCandidates()[nPreference].incrementVotes(vBallotArray[ nToTally[i] ]); 
00230 
00231                     addAuditLine(to_string(nRank) + " -> " + getCandidates()[nPreference].getName());
00232                     
00233                     if(getCandidates()[nPreference].getNumVotes() >= getDroopQuota()){ 
00234                         addWinner(&getCandidates()[nPreference]); 
00235                         if(getNumWinners() == getNumSeats()) { handleWin(); return; } // stop runoff 
00236                     } 
00237                 } 
00238             } 
00239         }
00240         
00241     }
00242     delete[] nToTally;
00243 
00244     // ****************************************************************************
00245     // *                 If here, no one has reached droop quota,                 *
00246     // *   so make the top number of seats candidates the winners by popularity   *
00247     // ****************************************************************************
00248 
00249     sort(getCandidates().begin(), getCandidates().end(),[](auto a, auto b) { return a.getNumVotes() > b.getNumVotes(); } ); 
00250 
00251     // ****************************************************************************
00252     // *               Add top number_of_seats candidates to winners              *
00253     // ****************************************************************************
00254 
00255     for(int i = 0; i < getNumSeats(); i++){ 
00256         addWinner(&(getCandidates()[i])); 
00257     } 
00258 
00259     handleWin(); 
00260 }
