00350 void IRV::digest(string buff){ // Takes in a line
00351     
00352     unsigned short ranking = 0;
00353     unsigned short c = 1;
00354     unsigned short tallied = -1;
00355 
00356     unsigned short MIN_CANDIDATES = (short)(((float)getNumCandidates() / 2.0) + 0.5);
00357     
00358         for (int i = 0; i < buff.length(); i++){
00359             if(buff[i] == ','){             
00360                 if(i == 0 || buff[i - 1] == ',') { c++; }   // check if first char, and check if last char was ','
00361                 continue;                                   // else continue
00362             } else { // build ranking number                
00363                 int nLine = buff[i] - '0';
00364                 ranking += (c * quick_pow10((getNumCandidates() - nLine)));
00365 
00366                 if( nLine == 1) { getCandidates()[c - 1].incrementVotes(); tallied = c - 1; } // Add Tally
00367                 c++;
00368             } 
00369         }
00370 
00371         if(ranking % quick_pow10(getNumCandidates() - MIN_CANDIDATES + 1) == 0){ 
00372             invalidateBallot(buff); // Invalidate ballot
00373             getCandidates()[tallied].decrementVotes(); // Remove tally
00374         }
00375         else{ vBallotArray[ Combinations::indexOf(ranking, getNumCandidates()) ] += 1; } // Add Ranking 
00376         
00377 }
