00383 void IRV::handleWin(){
00384     // ****************************************************************************
00385     // *                           Log Round of Tallies                           *
00386     // ****************************************************************************
00387 
00388     logCandidates();
00389     
00390     // ****************************************************************************
00391     // *                      Add not winners to loser vector                     *
00392     // ****************************************************************************
00393     
00394     for (int i = 0; i < getNumCandidates(); i++){
00395         if(!getCandidates()[i].isWinner()){
00396             addLoser(&getCandidates()[i]);
00397         }
00398     }
00399 }
