00403 void IRV::writeToAudit(string s){
00404     time_t t = time(nullptr);
00405     if(s.length() == 0){
00406         s = "IRVElectionAudit" + to_string(t) + ".txt";
00407     }
00408     ofstream auditFile(s);
00409     if(auditFile.good()){
00410         if(SHUFFLE) { auditFile << "SHUFFLE Enabled\n"; }
00411         else        { auditFile << "SHUFFLE Disabled\n"; }
00412 
00413         auditFile   << "Election type: IRV\n"
00414                     << "Election was ran on: "<< asctime(localtime(&t)) << '\n';
00415         
00416         streambuf *psbuf, *backup;
00417 
00418         backup = std::cout.rdbuf();     // back up cout's streambuf
00419 
00420         psbuf = auditFile.rdbuf();      // get file's streambuf
00421         cout.rdbuf(psbuf);              // assign streambuf to cout
00422 
00423         displayResults(false);          // Prevent colors
00424 
00425         cout.rdbuf(backup);             // restore cout's original streambuf
00426 
00427         auditFile << "\nBallot Allocations: \n";
00428         for (int i = 0; i < audits.size(); i++){
00429             auditFile << audits[i] << '\n';
00430         }
00431         auditFile << "--- END OF AUDIT FILE ---";
00432 
00433         auditFile.close();
00434         cout << CYAN << "Audit file created at: " + s + "\n" << RESET;
00435     }else{
00436         cout << RED << "ERROR: Creating audit file failed!\n" << RESET;
00437     }
00438     return;
00439 }
