00064 void OPL::runOff(){
00065     // ****************************************************************************
00066     // *                          Calculate Droop Quota                           *
00067     // ****************************************************************************
00068 
00069     setDroopQuota(getNumBallots() / getNumSeats());
00070 
00071     // ****************************************************************************
00072     // *                               Sort by votes                              *
00073     // ****************************************************************************
00074 
00075     sort(vParties.begin(), vParties.end(), [](Party &a, Party &b) { 
00076         if(a.getNumVotes() != b.getNumVotes()){ return a.getNumVotes() > b.getNumVotes(); }
00077         return Election::flipCoin();
00078     });
00079 
00080 
00081     // ****************************************************************************
00082     // *                               Initial Tally                              *
00083     // ****************************************************************************
00084 
00085     for (Party &i : vParties){
00086         int nSeats = i.getNumVotes() / getDroopQuota();
00087         i.incrementSeats( min(getNumSeats(), nSeats) );
00088         setNumberOfSeats( max(getNumSeats() - nSeats, 0) );     
00089         i.setRemainder(i.getNumVotes() % getDroopQuota());
00090         i.log( i.getNumSeats() );
00091         i.log( i.getRemainder() );
00092     }
00093 
00094     // ****************************************************************************
00095     // *                             Sort by remainder                            *
00096     // ****************************************************************************
00097 
00098     sort(vParties.begin(), vParties.end(), [](Party &a, Party &b) { 
00099         if(a.getRemainder() != b.getRemainder()){ return a.getRemainder() > b.getRemainder(); }
00100         return Election::flipCoin();
00101     });
00102 
00103     // ****************************************************************************
00104     // *                        Distribute remaining seats                        *
00105     // ****************************************************************************
00106 
00107     for(Party &i : vParties){
00108         i.incrementSeats( min(getNumSeats(), 1) );
00109         i.log(min(getNumSeats(), 1));
00110         setNumberOfSeats( max(getNumSeats() - 1, 0) );
00111     }
00112     
00113 }
