00167 void OPL::digest(string buff){
00168     unsigned int pos = 0;
00169     for (int i = 0; i < buff.length(); i++){
00170         if(buff[i] == ',') { 
00171             if(i == 0 || buff[i - 1] == ',') { pos++; }
00172             continue;
00173         }else { 
00174             getCandidates().at(pos).incrementVotes();
00175             getParty(getCandidates().at(pos).getParty())->incrementVotes();
00176             pos++; 
00177         }
00178     }
00179 }
