00215 void OPL::processHeaders(){
00216     if(getHeader().size() != 5) {
00217         cout << RED <<"ERROR. Unexpected header size\nExpected 5, found " << getHeader().size() << '\n' << RESET;
00218         exit(-1);
00219     }
00220     setElectionType(getHeader()[0]);
00221     setNumberOfCandidates(stoi(getHeader()[1]));
00222     setNumberOfSeats(stoi(getHeader()[2]));
00223     addBallots(stoi(getHeader()[3]));
00224     
00225     stringstream cStream(getHeader()[4]);
00226     while(cStream.good()){
00227         string candidate;
00228         string party;
00229         getline(cStream, candidate, '/');
00230         getline(cStream, party, ',');
00231         /*
00232             addParty(string p) => checks if p is in array, if not creates new party, if does not create new party.
00233             getParty(string p) => returns index to party matching string p if string p does not exist returns nullptr.
00234 
00235         */
00236         addParty(party);
00237         addCandidate(Candidate(candidate, party)); //add the candidate to the candidates vector         
00238     }
00239 }
