00241 void OPL::addParty(string p){ // adds unique
00242     // Check if p exists
00243     for (Party &i : vParties){
00244         if(i.getPartyName() == p) { return; } // found, we do nothing
00245     }
00246     vParties.push_back(Party(p));
00247 }
